import React from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity, Image, KeyboardAvoidingView, Alert } from 'react-native';
import imageLogo from "../assets/images/logo.png";
import constants from '../config/constants';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import AccountBoxIcon from '@material-ui/icons/AccountBox';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      '& .MuiTextField-root': {
        margin: theme.spacing(1),
        width: '25ch',
      },
    },
  }),
);

export default class LoginScreen extends React.Component {
  constructor(props) {
    super(props);
      this.state = {
        token: '',
        user_email: '',
        user_nicename: '',
        user_display_name:'',
        email:'',
        password:''
      };
  }
  login = () => {
    console.log("Iniciando Sesión");
    let email = this.state.email;
    let passwd = this.state.password;

    fetch(constants.API_URL + 'jwt-auth/v1/token', {
        method: 'POST',
        headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            username: email,
            password: passwd,
        }),
    }).then((response) => response.json()
    ).then((r) => {
          if(r.token){
            this.setState({
              token: r.token,
              user_email: r.user_email,
              user_nicename: r.user_nicename,
              user_display_name: r.user_display_name
            });
            this.props.navigation.navigate('Inicio')
          }
          else if(r.data.status == 403){
            Alert.alert( 'Error','Nombre de usuario o Contraseña incorrecta');
          }

         console.log(r);
    }).catch((error)=>{
        console.log(error.message)
    })
    ;


  };

  render(){

    return (
    <KeyboardAvoidingView
        style={styles.container}
        behavior="padding"
        enabled                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
      >
      <View style={styles.container}>
        <Image source={imageLogo} style={styles.logo} />
        <View style={styles.inputView} >
          <TextField id="email" label="Correo Electrónico" onChangeText={text => this.setState({email:text})} />
        </View>
        <View style={styles.inputView} >
          <TextField id="passwd" type="password" label="Password" onChangeText={text => this.setState({password:text})} />
        </View>
        <Button
            variant="contained"
            color="primary"
            endIcon={<AccountBoxIcon />}
            onPress={this.login}
        >
            Iniciar Sesión
      </Button>
        <TouchableOpacity style={styles.loginBtn} onPress={this.login}>
          <Text style={styles.loginText}>Iniciar Sesión</Text>
        </TouchableOpacity>


      </View>
    </KeyboardAvoidingView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    width:"100%",
    alignItems: 'center',
    justifyContent: 'space-between'
  },

  logo:{
    flex: 1,
    width: "30%",
    resizeMode: "contain",
    alignSelf: "center",
    top:-30
  },

  forgot:{
    color:"white",
    fontSize:11
  },
  loginBtn:{
    width:"80%",
    backgroundColor:"#fb5b5a",
    borderRadius:25,
    height:50,
    alignItems:"center",
    justifyContent:"center",
    marginTop:40,
    marginBottom:10
  },
  loginText:{
    color:"white"
  }
});
