import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, ScrollView, Image, KeyboardAvoidingView, Keyboard } from 'react-native';
import { TextInput, Button, Appbar, Dialog, Portal, Paragraph, Provider, DataTable } from 'react-native-paper';
import dismissKeyboard from 'react-native-dismiss-keyboard';
import constants from '../config/constants';

export default class MainScreen extends React.Component {
  constructor(props) {
    super(props);
      this.state = {
        order_data:{},
        order_id:'',
        token: '',
        error_visible:false,
        error_message:""
      };
  }
  _goBack = () => console.log('Went back');

  _handleSearch = () => console.log('Searching');

  _handleMore = () => console.log('Shown more');


  getOrderData = () => {
    dismissKeyboard();
    console.log(this.state.token);
    console.log(constants.API_URL + 'wc/v3/orders/' + this.state.order_id);
    fetch(constants.API_URL + 'wc/v3/orders/' + this.state.order_id, {
      method: 'GET',
      headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + this.state.token
      }

      }).then((response) => response.json()
      ).then((r) => {

          console.log(r);
      }).catch((error)=>{
          console.log(error.message)
      })
      ;
  }

  render() {
    const { navigation } = this.props; 
    const data = navigation.getParam('data', {});
    this.state.token = data.token;

    return (
      <Provider>
        <Portal>
          <Appbar.Header>
            <Appbar.Content
              title="Quickex"
              subtitle={"Bienvenido " + data.user_display_name}
            />
            <Appbar.Action icon="dots-vertical" onPress={this._handleMore} />
          </Appbar.Header>
          <View style={styles.container}>
            <View style={styles.inputView}>
              <TextInput label='Orden de Compra' placeholder="Ingrese Numero de orden" keyboardType="number-pad" style={styles.inputText} onChangeText={text => this.setState({order_id:text})}/>
            </View>
            <View style={styles.inputView}>
                <Button icon="account-box" mode="contained" onPress={this.getOrderData}>
                  Buscar Orden
                </Button>
            </View>
            <View style={styles.inputView}>
              <DataTable>
                <DataTable.Row>
                  <DataTable.Title>Orden Número</DataTable.Title>
                  <DataTable.Cell>{this.state.order_data.number}</DataTable.Cell>
                </DataTable.Row>
                <DataTable.Row>
                  <DataTable.Title>Estado</DataTable.Title>
                  <DataTable.Cell>{this.state.order_data.id}</DataTable.Cell>
                </DataTable.Row>
              </DataTable>  
            </View>  
          </View>

        </Portal>
      </Provider>

    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    width:"100%",
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'space-between'
  },

  logo:{
    flex: 1,
    width: "30%",
    resizeMode: "contain",
    alignSelf: "center",
    top:-30
  },
  inputView:{
    width:"80%",
    backgroundColor:"#fff",
    marginTop:20,
    justifyContent:"center",

  },
  inputText:{
    backgroundColor:"#fff",
    color:"#fff"
  },
});
