import React from 'react';
import { StyleSheet, Text, View, Image, KeyboardAvoidingView, Keyboard } from 'react-native';
import imageLogo from "../assets/images/logo.png";
import constants from '../config/constants';
import { TextInput, Button, Dialog, Portal, Paragraph, Provider } from 'react-native-paper';
import dismissKeyboard from 'react-native-dismiss-keyboard';

export default class LoginScreen extends React.Component {

  constructor(props) {
    super(props);
      this.state = {
        email:'',
        password:'',
        error_visible:false,
        error_message:""
      };
  }
  _showDialog = () => this.setState({ error_visible: true });
  _hideDialog = () => this.setState({ error_visible: false });

  login = () => {
    dismissKeyboard();
    console.log("Iniciando Sesión");
    let email = this.state.email;
    let passwd = this.state.password;

    fetch(constants.API_URL + 'jwt-auth/v1/token', {
        method: 'POST',
        headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            username: email,
            password: passwd,
        }),
    }).then((response) => response.json()
    ).then((r) => {
          if(r.token){
            this.props.navigation.navigate('Inicio', {data: r});
          }
          else if(r.data.status == 403){
            this.setState( {error_message:'Nombre de usuario o Contraseña incorrecta', error_visible:true});
          }

         console.log(r);
    }).catch((error)=>{
        console.log(error.message)
    })
    ;


  };
  render(){
    return (


      <Provider>
        <Portal>
          <KeyboardAvoidingView
            style={styles.container}
            behavior="padding"
            keyboardVerticalOffset={30}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
          >
          <View style={styles.container}>
            <Image source={imageLogo} style={styles.logo} />
            <View style={styles.inputView} >
              <TextInput label='Correo Electrónico' style={styles.inputText}  value={this.state.email} onChangeText={text => this.setState({email:text})}/>
            </View>
            <View style={styles.inputView} >
            <TextInput label='Password' secureTextEntry={true} autoCorrect={false} style={styles.inputText} value={this.state.password} onChangeText={text => this.setState({ password:text })}/>
            </View>
              <Button icon="account-box" mode="contained" onPress={this.login}>
                Iniciar Sesión
              </Button>
          </View>
          <Dialog
             visible={this.state.error_visible}
             onDismiss={this._hideDialog}>
            <Dialog.Title>Error</Dialog.Title>
            <Dialog.Content>
              <Paragraph>{this.state.error_message}</Paragraph>
            </Dialog.Content>
          </Dialog>
          </KeyboardAvoidingView>
        </Portal>
      </Provider>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    width:"100%",
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'space-between'
  },

  logo:{
    flex: 1,
    width: "30%",
    resizeMode: "contain",
    alignSelf: "center",
    top:-30
  },
  inputView:{
    width:"80%",
    backgroundColor:"#fff",
    marginTop:-10,
    height:40,
    marginBottom:20,
    justifyContent:"center",

  },
  inputText:{
    backgroundColor:"#fff",
    color:"#fff"
  },
});
